INSERT INTO `articlesection` (`id`, `page_id`, `num`, `head`, `text`, `extra`, `stylekey`, `arrange`, `image`, `image_alt`, `promolink`, `promoname`) VALUES
(1, 3, 1, 'Определение', 'Телесно-ориентированная психотерапия - направление, ориентированное на решение психологических проблем путем воздействия на тело человека. \r\nЭтот вид психотерапии, так же известный как телесная психотерапия или попросту \"телеска\", основывается на концепции, что психологические стрессы оставляют физиологический след в организме в виде мышечных блоков. Воздействие на этот след с помощью специальных упражнений помогает вновь пережить душевное напряжение, вызвавшее образование мышечного зажима и высвободить его. В результате появляется возможность докопаться до корней душевных травм, удалить их последствия и открыть заблокированные энергетические потоки.', '', '', 'center', '', '', '', ''),
(2, 3, 2, 'Решаемые проблемы', 'С помощью разработанных комплексов упражнений прорабатываются как осознанные проблемы, так и спрятанные глубоко в подсознании, то есть проблемы, которые человек даже не осознает.\r\nПри правильном применении телесной психотерапии исцеляются такие расстройства как чувство страха, ощущение постоянной тревоги, синдром хронической усталости, неудовлетворённость, нездоровое чувство стыда и т.д., повышается уверенность в своих силах, улучшается общее физическое состояние. В процессе терапии пациент глубоко познает себя, свое тело и избавляется от ненужных установок.', '', '', 'center', '', '', '', ''),
(3, 3, 3, 'Методы', 'Телесная психотерапия объединяет все методы, нацеленные на изучение тела и осознание телесных ощущений. Наиболее распространены следующие методики:', '', '', 'center', '', '', '', ''),
(4, 3, 4, 'Характерологический анализ (Вильгельм Райх)', 'Трехуровневая структура личности: <br>\r\n 1 Поверхностный уровень - межличностные коммуникации под воздействием социальных ценностей общества <br>\r\n 2 Промежуточный уровень - импульсы агрессии и влечения либидо <br>\r\n 3 Глубинный уровень - природные человеческие импульсы, эмоциональное здоровье и искренняя любовь <br>\r\nМышечные зажимы блокируют эмоции - тревогу, гнев и сексуальное возбуждение.\r\nМышечный панцирь и психологический панцырь - одно и то же.\r\nЗащитный панцырь блокирует энергетические потоки - основу сильных эмоций, не дает человеку активно переживать, ограничивает и искажает выражение чувств. Блокированные эмоции не проходят, поскольку не проявляются и не испытываются в полной мере. Мышечные зажимы могут подавлять и естественные сексуальные чувства, мешать испытанию полноценного оргазма.\r\nОсвобождение от заблокированной эмоции возможно только путем ее глубокого переживания.', '', 'small_txt', 'center', '', '', '', ''),
(5, 3, 5, 'Биоэнергетический психоанализ (Александр Лоуэн)', 'Развитие теории мышечного зажима Райха. В отличие от метода Райха, использующего расслабление мышц для борьбы с мышечным напряжением, Лоуэн предложил активизировать эмоции последовательным расслаблением и напряжением мышц. \r\nЕсли в концепции Райха акцентируется сексуальность, Лоуэн фокусируется на сердце человека.<br>\r\nЧеловек рожден для того, что бы принимать и отдавать любовь. Если человек долго не получает любовь - со временем он принимает ее отсутствие как данность. Тогда человек отказывается от попыток достижения любви, подавив негативные эмоции связанные с недостатком любви. <br>\r\nНакапливающиеся негативные эмоции становятся привычными, а за тем и неосознанными. Подавленные импульсы действуют напряжение в мышцах, что приводит к мышечной блокировке. Тело становится инструментом самоограничения и подавления своих естественных желаний.<br>\r\nБиоэнергетика направлена на разрешение эмоциональных конфликтов, ведущих к хронической напряженности. Хроническая напряженность в свою очередь это источник хронической усталости, неудовлетворенности собственной жизнью и недостатка жизненных сил.\r\nИзбавление от мышечного панциря высвобождает жизненные силы для действительно важных функций. Это позволяет человеку наслаждаться собственным телом, а не просто использовать его как оболочку для сознания.', '', 'small_txt', 'center', '', '', '', ''),
(6, 3, 6, 'Биосинтез (Дэвид Боаделла)', 'Взгляд на идею мышечного зажима Райха с точки зрения эмбриологии (развитие ребенка до рождения).\r\nВ первую неделю жизни эмбриона формируются три листка: эндодерма, мезодерма и эктодерма из которых развивается ребенок. С этими листками связаны  соответствующие потоки энергии, а нарушения приводят к блокам - панцирям.\r\nИз эндодермы развиваются органы дыхания и пищеварения, обмен веществ. (энергетический поток - эмоции, нарушения - висцеральный панцирь)<br>\r\nИз мезодермы развиваются кости мышцы и кровеносная система. (энергетический поток - действия, нарушения - мышечный панцирь)<br>\r\nИз эктодермы развивается кожа, нервы и мозг, органы чувств. (энергетический поток - мышление, нарушения - церебральный панцирь)<br>\r\nСупер-эго не имеет органической основы и его формирует влияние общества<br>\r\nИзначально все три листка и потока энергии объединены в единое целое и органично взаимодействуют между собой. С течением внутриутробного развития, при рождении и в младенчестве происходят стрессы и травмы нарушающие гармоничное взаимодействие энерго-потоков. В результате действие отделяется от мышления и эмоций, либо эмоции от действий и разума, либо мысли от действий и эмоций. <br>\r\nТрехуровневая структура личности:<br>\r\n1 Поверхностный уровень - маска, защитный панцирь характера, формирующийся для защиты целостности личности.<br>\r\n2 Второй уровень - гнев, тоска, тревога, страх, обида, чувство одиночество.<br>\r\n3 Основной уровень, ядро личности - доверие, благополучие, любовь, радость <br>\r\nНеправильное развитие ядра создает второй уровень, подавление отрицательных эмоций второго уровня создает маску поверхностного уровня.<br>\r\nБиосинтез направлен на снятие блоков, на интеграцию тело - чувства - разум, на целостное развитие человека.', '', 'small_txt', 'center', '', '', '', ''),
(7, 3, 7, 'Формы терапии', 'Телесно-ориентированная психотерапия может практиковаться в различных формах: индивидуальная терапия, групповая терапевтическая сессия, тренинг (интенсив). Наиболее эффективная формой является <strong>тренинг</strong>, на 1-2 дня пациент полностью погружается в собственные ощущения и решает наиболее важные душевные проблемы. Тренинги телесной психотерапии могут быть как чисто женскими или мужскими, так и смешанными. Оптимальная аудитория тренинга от 8-ми до 20-ти человек.\r\n\r\nНа занятиях практикуются индивидуальные, парные и групповые телесные упражнения, по этому желательно иметь свободную одежду в которой удобно лежать, сидеть и двигаться на полу - например, спортивный костюм.', '', '', 'center', '', '', '', ''),
(8, 3, 8, 'Упражнения', 'В практиках используются различные физические упражнения, массаж, техники активной медеитации. Часто элементы телесной терапии чередуют с дыхательными техниками, холотропным дыханием. Для получения максимального результата, каждое упражнение направляется не только на расслабление мышц, но и на осознание своего тела и эмоциональное реагирование. <strong>Важно сопровождать упражнения телесной терапии анализом и самопознанием.</strong> Сами телесные упражнения будут иметь временный результат, если не сопровождаются осознанием и связанным с ним психологическими изменениями. Именно комплексный подход ведет к исцелению. ', '', '', 'center', '', '', '', ''),
(9, 3, 9, 'В заключение', '<strong>Телесно-ориентированная терапия</strong> направлена не только на решение психологических проблем, но и на общее оздоровление организма. В результате практик высвобождается большое количество жизненной энергии, повышается уверенность в себе и общий жизненный тонус.\r\nПоймите свое тело. Осознайте истинные желания. Научитесь получать удовольствие, радость и истинное счастье от жизни. <br>\r\nНачните перемены сейчас и жизнь изменится к лучшему.', '', '', 'center', '', '', 'master', 'Телесно-ориентированные терапевты'),
(10, 4, 1, 'Википедия гласит:', 'Психологический тренинг — форма активного обучения навыкам поведения и развития личности. В тренинге участнику предлагается проделать упражнения, ориентированные на развитие или демонстрацию психологических качеств или навыков. Ключевым принципом, обеспечивающим эффективное обучение и развитие, является постоянное сочетание в тренинге всех форм деятельности: общение, игра, обучение, труд.', '', '', 'right', 'happy_wikipedia_logo.png', 'Википедия', '', ''),
(11, 4, 2, 'Портал Психологос:', 'Психологический тренинг - личностный тренинг, проводимый психологом и с использованием психологических, а не эзотерических и мистических понятий и категорий.', '', '', 'right', 'pt_psychologos.png', 'Психологос', '', ''),
(12, 4, 3, 'Наш вариант значения:', 'Психологический тренинг - познавательный процесс в виде организации физической и ментальной активности человека для достижения необходимых изменений во внутреннем мире.', '', '', 'right', 'happy_NS_logo_ds.png', 'Наше Счастье', 'feedback', 'Предложить свой вариант'),
(13, 4, 4, 'Решаемые проблемы', 'Действие психологического тренинга сугубо индивидуально и напрямую зависит от степени погруженности участника в процесс. \r\nСреди полученных результатов чаще всего бывают позитивные изменения в запросах участников. Например избавление от страха и тревоги, хронической усталости, раскрытие сексуальности, обновление отношений с партнером, сближение с семьей, гармонизация личной жизни, расширение спектра ощущений, удачное зачатие, самореализация, повышение самооценки, достижение счастья и радости в жизни и т. д.\r\n', '', '', 'left', 'pt_trouble.png', 'психологические тренинги - решаемые проблемы', '', ''),
(14, 4, 5, 'Основные подходы', 'Количество видов психотерапии сегодня измеряется сотнями, тем не менее большинство видов можно разделить по направлениям в соответствии с подходом к лечению.', '', '', 'center', '', '', '', ''),
(15, 4, 6, 'Психодинамический подход', '<span class=\"way_to_hapiness\">Суть:</span>\r\nПроцессы психики регулируются не внешними обстоятельствами, разумом или волей человека, а взаимодействием и противостоянием (динамикой) внутренних психических сил, полученных в результате ранних переживаний. Наиболее ранние внутренние эмоциональные конфликты определяют точку зрения на весь последующий опыт и воспроизводятся в последующей жизни. Таким образом основные причины любого поведения лежат в подсознании и детских переживаниях.', '<span class=\"way_to_hapiness\">Используется:</span>\r\nВ тренингах самопознания и саморазвития, интенсивах телесно-ориентированной терапии и др.', 'psychotraining_method', 'right', 'pt_babybrain.png', 'Психодинамический подход - детские переживания в психологических тренингах', '', ''),
(16, 4, 7, 'Поведенческий (бихевиоральный) подход', 'Подход в процессе которого предметом изучения является поведение.\r\n<span class=\"way_to_hapiness\">Суть:</span>\r\nВ основе всего лежит человеческое поведение в ответ на внешние обстоятельства. Психологические проблемы возникают при искажении объективной реальности в результате ошибочных субъективных оценок, предпосылок и обобщений.\r\nОсновная задача терапевта - найти искажения реальности, научить правильной оценке ситуации и изменить поведение в необходимое русло.\r\nВ рамках поведенческого подхода, перед терапевтом стоит 4 основных вопроса: \r\n\r\n1. Какое поведение необходимо изменить?\r\n2. Какие события поддерживают это поведение?\r\n3. Какие вмешательства могут изменить это поведение?\r\n4. Как закрепить измененное поведение и распространить на новые ситуации?\r\n', '<span class=\"way_to_hapiness\">Используется:</span>\r\nВ корпоративных и семейных тренингах. ', 'psychotraining_method', 'right', 'pt_behavior.png', 'Бихевиоральный подход в психологических тренингах', '', ''),
(17, 4, 8, 'Когнитивный подход', 'Подход в процессе которого предметом изучения являются познавательные (когнитивные) процессы мышления.\r\n<span class=\"way_to_hapiness\">Суть:</span>\r\nЧеловек стремится понять свои прошлые переживания и контролировать будущие основываясь на личном опыте. Поскольку любое событие можно увидеть с многих точек зрения, человек имеет богатые возможности в интерпретации внутреннего мира и внешних событий.  В итоге каждый по своему осознает объективную реальность. \r\nДля прогнозирования хода событий жизни, человек создает субъективные гипотезы относительно реальности и строит планы на основании ожидаемых результатов. Таким образом каждый из нас создает собственные познавательные модели для построения непротиворечивой картины мира. И сбои в этой системе ведут к неработающим познавательным схемам.\r\nВ контексте этого подхода, личность воспринимается в виде системы познавательных схем, используемых человеком для интерпретации внутреннего мира и прогнозирования будущих событий. Задачей терапевта является изменение ошибочных познавательных схем мышления.', '<span class=\"way_to_hapiness\">Используется:</span>\r\nПрименяется фрагментарно в корпоративных и других психологических тренингах.', 'psychotraining_method', 'right', 'pt_cognitive.png', 'Когнитивный подход в психологических тренингах', '', ''),
(18, 4, 9, 'Гуманитарный (экзистенциально-гуманистический) подход', '<i>exsistentia (лат) - существую </i>\r\nПодход, фокусирующийся на осмыслении жизни.\r\n<span class=\"way_to_hapiness\">Суть:</span> \r\nВ центре внимания терапевта находится осмысление человеком своего существования, осознание жизненных ценностей, изменение жизни на основании этих ценностей и принятие ответственности за сделанный выбор. \r\nЧеловек воспринимается как единство тела, эмоциональных переживаний и сознания. В рамках подхода нет явного разделения между внутренними переживаниями и личностью, поскольку в каждый момент человек отождествляет себя со своими переживаниями. \r\nЗа любыми психологическими трудностями лежат глубинные проблемы существования: свобода выбора, ответственность, взаимосвязи с семьей и обществом, поиск себя, самореализация, смысл жизни  и т. д. \r\nОсновная задача терапевта - понять человека таким, каким он живет внутри своей реальности, помочь осмыслить свою жизнь и начать жить более полно.', '<span class=\"way_to_hapiness\">Используется:</span>\r\nВ тренингах самопознания и саморазвития, творческого самовыражения, интенсивах телесно-ориентированной терапии и др.', 'psychotraining_method', 'right', 'pt_existance.png', 'экзистенциально-гуманистический подход в психологических тренингах', '', ''),
(19, 4, 10, 'Системный подход', 'Подход фокусируется на связях и межличностных отношениях в определенной группе людей.\r\n<span class=\"way_to_hapiness\">Суть:</span>\r\nОтдельный индивидуум не является объектом терапии, таковым является система взаимоотношений (семья, коллектив, группа людей). Система - нечто большее, чем сумма отдельных элементов, это отдельный организм со своей историей, динамикой, этапами развития и ценностями. Неработающая система ведет к проблемам ее участников.  \r\nТерапевт занимает позицию наблюдателя или тренера. Основная задача - выявить проблемные взаимосвязи, объяснить ошибки участникам, найти пути оздоровления отношений. ', '<span class=\"way_to_hapiness\">Используется:</span>\r\nВ корпоративных и семейных психологических тренингах.', 'psychotraining_method', 'right', 'pt_system.png', 'Системный подход в психотерапии', '', ''),
(20, 4, 11, 'Интегративный подход', 'Подход, включающий в себя необходимые части разных подходов исходя из максимальной эффективности для конкретного человека.\r\n', '<span class=\"way_to_hapiness\">Используется:</span>\r\nВ любых психологических тренингах.', 'psychotraining_method', 'right', 'pt_integrative.png', 'Интегративный подход в психологических тренингах', '', ''),
(21, 4, 12, 'Желаем удачи!', 'Страница находится в процессе наполнения,\r\nмы постарались собрать наиболее актуальную информацию относительно психологических тренингов. \r\n', '', 'text-center', 'center', '', '', '', ''),
(22, 5, 1, 'Активные медитации - что это такое', 'Активные медитации - техники расслабления через движение, чаще всего выполняемые с закрытыми глазами. Родоначальником активной медитации считается индийский духовный лидер Ошо. Этот метод медитации используется для отключения от насущных проблем и освобождения тела от тотального контроля мозгом. Часто используется как эффективное очищение от тревог и подготовка к следующей медитации или психологической работе. \r\n\r\n	В процессе медитации движения лучше производить бессистемно и хаотически. Любой ритм это система, мозг реагирует и начинает как обычно все систематизировать. Именно от подобного контроля освобождает активная медитация. \r\n\r\n	Ошо разработал множество активных медитаций, самая мощная и известная из которых - “Динамическая медитация”. Из остальных активных медитаций Ошо можно выделить медитацию Кундалини (тряска), Натарадж (танец) и Надабрама (гудение),  которые так же основаны на движениях, но менее энергичны нежели Динамическая медитация.', '', '', 'center', '', '', '', ''),
(23, 5, 2, 'Динамическая медитация', 'Динамическая медитация - наиболее популярная из активных медитаций Ошо. Эта взрывная техника призвана сломать укоренившиеся схемы телесных реакций, понаблюдать за собой со стороны, ощутить скрытую свободу, тишину и покой. Медитация выполняется под специально написанную для нее музыку, длится 1 час и разделена на 5 стадий: дыхание, катарсис, мантра, наблюдение и радость.\r\n\r\nВ идеале делается ранним утром - когда природа оживает, уходит ночь, приходит солнце и все вокруг пробуждается.', '', '', 'center', '', '', '', ''),
(24, 5, 3, 'Предварительная подготовка', 'Глаза необходимо держать закрытыми во время всей медитации, для этого обычно используют повязки на глаза. Медитировать можно как в одиночестве, так и в группе, но групповой эффект сильнее. \r\n\r\n	Медитация предполагает шум, то есть помещение желательно подбирать соответствующее - без чувствительных к шуму соседей. Если шуметь все же нежелательно, можно медитировать тихо, направляя энергию внутрь - вместо издания звуков, в катарсисе можно более энергично двигаться, а звук “Ху\" в третьей стадии ударять мысленно, про себя. ', '', '', 'center', '', '', '', ''),
(25, 5, 4, 'Стадии динамической медитации', '<strong>Стадия 1 - Дыхание</strong>\r\n<i>длительность : 10 минут</i>\r\n\r\nЭнергичное хаотичное дыхание через нос. Позволь дыханию быть интенсивным, глубоким и быстрым. Любой ритм нежелателен, фокус на выдох - о вдохе тело позаботится само. Дыхание должно происходить глубоко в легких. Дыши так глубоко и часто, насколько возможно, пока ты сам не станешь дыханием. Используй в помощь естественные движения тела, для сохранения и повышения внутренней энергии. Чувствуй наполнение энергией, но не расходуй на этой стадии.\r\n\r\n<strong>Стадия 2 - Катарсис</strong>\r\n<i>длительность : 10 минут</i>\r\n\r\nВзорвись! Позволь выйти всему что должно быть выброшено. Следуй за телом. Дай своему телу свободу выражать все что есть. Стань абсолютно безумным. Кричи, вой, плач, прыгай, бей, тряси, танцуй, пой, смейся, разбрасывай себя вокруг. Ничего не оставляй, позволь всему телу двигаться. Для начала обычно достаточно небольшого движения. Не позволяй своему разуму вмешиваться в происходящее! Сознательно прими безумство. Будь в полном погружении.\r\n\r\n<strong>Стадия 3 - Мантра</strong>\r\n<i>длительность : 10 минут</i>\r\n\r\nС поднятыми руками над головой, прыгай вверх - вниз, крича \"Ху! Ху! Ху!” так глубоко, насколько возможно. Приземляйся на всю стопу сразу, позволь звуку ударять молотом глубоко в сексуальный центр. Отдай все что есть, из последних сил, изнури себя полностью.\r\n\r\n<strong>Стадия 4 - Наблюдение</strong>\r\n<i>длительность : 10 минут</i>\r\n\r\nСтоп! Замри где ты оказался, в какой бы позиции ты себя ни нашел. Не управляй своим телом ни каким образом. Кашель, движение, что угодно - рассеет поток энергии и достижение будет утеряно. Наблюдай за всем что происходит с тобой.\r\n\r\n<strong>Стадия 5 - Радость</strong>\r\n<i>длительность : 15 минут</i>\r\n\r\nРадуйся! С музыкой и танцем вырази все что есть. Неси свою жизнерадостность с собой через весь день.', '', '', 'center', '', '', '', ''),
(26, 5, 5, 'Результаты', 'Динамическая медитация Ошо помогает от бессонницы, повышенной агрессивности и депрессии, повышает внутреннюю мотивацию и сострадание. Помогает в случаях эмоционального истощения. Улучшает выносливость и устойчивость к психологическим перегрузкам, при постоянном применении дает ощущение счастья и радости жизни.', '', '', 'center', '', '', '', ''),
(27, 5, 6, 'Противопоказания', 'Этот метод противопоказан больным эпилепсией и психологическими заболеваниями, а так же лицам с врачебными ограничениями на физическую активность.', '', '', 'center', '', '', '', ''),
(28, 5, 7, 'Желаем счастья', 'Динамическая медитация - мощный метод внутреннего очищения. От всего сердца желаем тебе полностью погрузиться в нее и обновиться духовно и физически. Активные медитации Ошо часто используются в психологических тренингах и являются незаменимой частью практик телесной психотерапии наряду с дыхательными и телесными техниками.', '', '', 'center', '', '', 'about_us', 'Телесно-ориентированные терапевты'),
(29, 6, 1, '', 'Йогатерапия - лечение заболеваний с помощью практики йоги. В качестве инструментов используются как асаны и пранаямы, так и различные варианты медитативных техник — концентрация на телесных, аудиальных и визуальных объектах.', '', '', 'right', 'mc_yoga_therapy.jpg', 'Йогатерапия', '', ''),
(30, 7, 1, '', 'Бессознательное или не осознаваемое — совокупность психических процессов не видимых сознанием, в отношении которых отсутствует осознанный контроль.', '', '', 'right', 'mc_unconscious.jpg', 'Бессознательное', '', ''),
(31, 8, 1, 'Самопознание.ру', 'Независимая афиша тренингов и семинаров по личностному росту.\r\n<span class=\"quote_author\">На сайте можно найти самые разные мероприятия, связанные с саморазвитием, психологией и самосознанием.</span>', '', '', 'center', '', '', 'http://samopoznanie.ru/', 'Перейти на сайт'),
(32, 8, 2, 'ПСИ-ФАКТОР', 'Информационный ресурс по научной и практической психологии.\r\n<span class=\"quote_author\">Библиотека прикладной психологии, консультации по психологическим вопросам, дистанционные курсы и семинары. Оригинальные обучающие программы для профессионалов и желающих сделать позитивные шаги в жизни и карьере.</span>', '', '', 'center', '', '', 'http://psyfactor.org/', 'перейти на сайт'),
(33, 8, 4, 'Tvoypsiholog.ru', 'Ежедневный психологический онлайн-журнал. \r\n<span class=\"quote_author\">База отличных статей по психологии, которые улучшат вашу жизнь.</span>', '', '', 'center', '', '', 'http://tvoypsiholog.ru/', 'перейти на сайт'),
(34, 8, 5, 'Психология', 'Психологический образовательный сайт.\r\n<span class=\"quote_author\">Сайт содержит полезную информацию (тесты, тренинги, книги и новости психологии) для всех, кто получает психологическое образование, интересуется психологией или профессионально ею занимается. </span> ', '', '', 'center', '', '', 'http://psychologiya.com.ua/', 'перейти на сайт'),
(35, 8, 6, 'mysites.ru: Психология', 'Каталог сайтов по психологии\r\n<span class=\"quote_author\">Обширная база ресурсов по различным направлениям.  </span>', '', '', 'center', '', '', 'http://www.mysites.ru/in.php?sid=423898&cat_key=Psihologiya', 'перейти на сайт');
