INSERT INTO `happypage` (`id`, `title`, `description`, `keywords`, `pagehead`, `pagedescription`, `imagelink`, `imagelink_alt`, `sendtopage`, `promolink`, `promoname`) VALUES
(1, 'Суть счастья', 'Все о счастье - определение, виды, пути достижения счастья, цитаты великих людей, символы счастья.', 'суть счастья, биохимия счастья, символы счастья, цитаты о счастье, песня счастье, счастье в философии, цветок счастья', 'Меню', 'Итак, ты интересуешься счастьем, это замечательно! Стать чуточку счастливей не повредит никому, даже людям уже имеющим радость, любовь и благополучие в своей жизни. Не говоря о тех, кто находится в отчаянии и ищет любой лучик радости. Для начала хорошая новость - любые мысли о счастье приносят радость, а следовательно делают нас чуть счастливей. Сейчас мы вместе разберемся в его природе: в чем суть, с чем едят и как стать еще счастливей. \r\nНиже собрана самая разнообразная информация о нем любимом, которая тем не менее не претендует на идеал. Если в тексте обнаружена ошибка, неточность, упущена важная информация, .', '', '', 'happiness/origin', 'happiness/origin', 'дальше'),
(2, 'Счастье - происхождение слова', 'Происхождение слова Счастье', 'счастье, слово, происхождение', 'Счастье - происхождение слова', '', '', '', 'happiness/meaning', 'happiness/meaning', 'определение'),
(3, 'Счастье - определение', 'Значение слова \"Счастье\"', 'счастье, значение, определение', 'Счастье - определение', '', '', '', 'happiness/biochemistry', 'happiness/biochemistry', 'биохимия счастья'),
(4, 'Биохимия счастья', 'Роль дофамина и эндогенных опиатов в создании ощущения счастья и радости.', 'счастье, биохимия, дофамин, эндорфины, эндогенные опиаты', 'Биохимия счастья', 'В ощущении кратковременного счастья основную роль играют дофамин и эндогенные опиоидные пептиды (эндогенные опиаты).', 'happy_dopamine.png', '', 'happiness/kinds', 'happiness/kinds', 'виды счастья'),
(5, 'Виды счастья', 'Все виды счастья с методами достижения, в классификации портала Наше Счастье.', 'счастье, виды счастья, классификация счастья, кратковременное счастье, счастье в жизни', 'Меню', 'Интернет пестрит классификациями счастья, но они либо устарели, либо не отражают сути. \r\nПоскольку нас они не удовлетворяют, ниже предлагаем наш вариант: кратковременное счастье мы разделили на телесное (ощущения, которые можно почувствовать телом) и моральное (то, что можно лишь осознать).', '', '', '', '', ''),
(6, 'Кратковременное  телесное (чувственное) счастье', 'Виды счастья которое можно ощутить физически - почувствовать телом.', 'счастье, почувствовать счастье, ощутить счастье, счастье тела', 'Кратковременное  телесное (чувственное) счастье', 'Это то самое счастье, которое можно почувствовать телом. \r\nКратковременное счастье часто связано с ограничениями - например городского жителя может осчастливить тишина деревни, а деревенского - запах метро или шум большого города. Человеку свойственно желать то, чего у него нет. И чем сильнее желание, тем ярче радость обретения. \r\nТем не менее, несмотря на различия в моральных ценностях, существуют общие тенденции, в силу человеческой физиологии.\r\n\r\nТелесное счастье основано на чувствах, а потому универсально и доступно любому здоровому человеку, вне зависимости от пола, рода деятельности, национальности и вероисповедания.', 'happy_calm.png', '', 'happiness/soul', 'happiness/soul', 'Душевное счастье'),
(7, 'Душевное счастье', 'Виды и способы достижения кратковременного душевного счастья.', 'душевное счастье, счастье в душе, счастье в сердце, способы достижения душевного счастья', 'Душевное <br>\r\n(кратковременное моральное) <br>\r\nсчастье ', 'Это счастье основано на субъективной оценке человека, поэтому не все виды доступны каждому и значения очень индивидуальны.', 'happy_crowd.png', '', 'happiness/life', 'happiness/life', 'Счастье в жизни'),
(8, 'Счастье в жизни (долговременное счастье)', 'Способы достижения счастья в жизни.', 'счастье, счастье в жизни, долговременное счастье, постоянная радость, способы достижения радости в жизни', 'Счастье в жизни <br>(долговременное счастье)', 'Счастье в жизни настолько индивидуально, что бессмысленно загонять его в рамки классификации. Ты либо ощущаешь его, либо нет. \r\nПриведем лишь несколько способов достижения этого максимального из возможных видов счастья.', 'happy_children.png', '', 'happiness/quotes', 'happiness/quotes', 'Цитаты о счастье'),
(9, 'Цитаты о счастье', 'Высказывания о счастье великих людей.', 'цитаты о счастье, высказывания о счастье', 'Цитаты о счастье', '', 'happy_giving.png', '', 'happiness/medicine', 'happiness/medicine', 'Счастье в медицине'),
(10, 'Счастье в медицине', 'Влияние счастья и радости на здоровье человека', 'счастье, радость, медицина', 'Счастье в медицине', '', 'happy_medicine.png', '', 'happiness/philosophy', 'happiness/philosophy', 'Счастье в философии'),
(11, 'Счастье в философии', 'Значение счастья в философских учениях, философия счастья.', 'счастье, философия счастья, счастье в философии', 'Счастье в философии', '', 'happy_aristotle.png', '', 'happiness/economy', 'happiness/economy', 'Счастье в экономике'),
(12, 'Счастье в экономике', 'Воздействие счастья на экономику страны', 'счастье, экономика', 'Счастье в экономике', '', 'happy_eko.png', '', 'happiness/symbols', 'happiness/symbols', 'Символы счастья'),
(13, 'Символы счастья', 'Список символов счастья в разных странах', 'символ счастья, символ удачи, подарок на удачу, подарок на счастье', 'Символы счастья', '', 'happy_bird_ru.png', '', 'happiness/holiday', 'happiness/holiday', 'День счастья'),
(14, 'День Счастья', 'Праздник счастья в календаре', 'день счастья, праздник счастья, международный день счастья', 'День Счастья', '', 'happy_topiary.png', '', 'happiness/museum', 'happiness/museum', 'Музей счастья'),
(15, 'Музей Счастья', 'Музей счастья в России - адрес, контакты, о проекте.', 'музей счастья, музей счастья в России', 'Музей Счастья', '', 'happy_museum.png', '', 'motivator', 'motivator', 'Мотиваторы'),
(16, 'Суть счастья', 'Все о счастье - определение, виды, пути достижения счастья, цитаты великих людей, символы счастья.', 'суть счастья, биохимия счастья, символы счастья, цитаты о счастье, песня счастье, счастье в философии, цветок счастья', 'Суть счастья', '', '', '', '', '', '');
INSERT INTO `happysection` (`id`, `page_id`, `head`, `image`, `image_alt`, `text`, `extra`, `arrange`) VALUES
(1, 2, '', 'happy_schasctye.png', 'Праславянское sъčęstь̂je', 'Праславянское sъčęstь̂je - \r\nсоставное из слов sъ- «хороший» и čęstь «часть», то есть «хорошая часть»', '', 'left'),
(2, 3, 'Википедия гласит:', 'happy_wikipedia_logo.png', 'Счастье - Википедия', 'Счастье — состояние, которое соответствует наибольшей внутренней удовлетворённости условиями своего бытия, полноте и осмысленности жизни, осуществлению своего человеческого назначения.', '', 'right'),
(3, 3, 'Толковый словарь Ожегова:', 'happy_ozeg.png', 'Счастье - Толковый словарь Ожегова', 'Счастье - \r\n1) Чувство и состояние полного, высшего удовлетворения.  \r\n2) Успех, удача. Во всём.', '', 'right'),
(4, 3, 'Наш вариант значения слова:', 'happy_NS_logo_ds.png', 'Наше Счастье', 'Для начала определимся в терминологии. Счастьем называют как краткие периоды эйфории, так и позитивное мироощущение в определенный период жизни. Дабы не смешивать эти абсолютно разные значения, мы назовем их кратковременное счастье и счастье в жизни.\r\n\r\nКратковременное счастье:\r\nОщущение полной и безграничной радости, затмевающей остальные ощущения.\r\n\r\nСчастье в жизни:\r\nСостояние полной удовлетворенности жизнью, ощущение радости и благодарности за происходящие события.', '', 'right'),
(5, 4, 'Дофамин', 'happy_dopamine.png', 'Дофамин формула', 'Нейромедиатор дофамин воздействует на дофаминовые рецепторы в синапсах мозга и вызывает чувство удовольствия (среди прочего - регуляция двигательной и половой активности, способности к обучению, принятия решений и др.). Подавляется системами серотонина и гамма - аминомасляной кислоты. \r\nВырабатывается при сексе, вкусной еде, приятных ощущениях и т. д., а так же при приятных воспоминаниях и предвкушении удовольствия.', '', 'left'),
(6, 4, 'Эндогенные опиоидные пептиды', 'happy_alpha_neoendorphin.png', 'Альфа Неоэндорфин формула', 'Под этим понятием скрываются эндорфины, энкефалины и другие пептиды. Для простоты их часто называют просто эндорфинами (расшифровывается как внутренний морфин). \r\nЭндорфины воздействуют на опоидные рецепторы мозга и оказывают противострессовое действие, повышают эмоциональный настрой, участвуют в ощущении эйфории, регулируют чувства боли и голода, связаны с выделением половых гормонов. Именно эндорфины отвечают за подъем настроения после успешно (без травм) перенесенного стресса. Механизм прост - все, что вырабатывается организмом для перенесения травмы и стресса, при отсутствии травмы, мы получаем бонусом: и обезболивание и эмоциональный подъем.\r\n \r\nХроническая усталость, постоянный стресс и хронические болезни - причины возникновения дефицита эндорфинов. Недостаток эндорфинов в организме в свою очередь ведет к подавленному настроению, депрессии, а так же неконтролируемому возбуждению (стресс по пустякам) и даже ускорению процесса старения. \r\n\r\nПовысить уровень эндорфинов можно занятиями спортом, сексом, прослушиванием субъективно нравящейся музыки, а так же смехом и получением свежих хороших впечатлений.\r\n\r\nКак только мы поднимаемся над сиюминутной эйфорией, в свои права вступает серотонин, нейромедиатор эмоциональной стабильности.', '', 'left'),
(7, 4, 'Серотонин', 'happy_serotonin.png', 'Серотонин формула', 'Выработка серотонина повышает настроение и эмоциональную устойчивость. Серотонин регулирует многие системы в организме, в том числе выработку дофамина, циркадные ритмы, аппетит, память, способность к обучению, двигательную активность, восприятие боли и др.\r\n\r\nС нарушением его работы связаны такие заболевания как шизофрения и депрессия. Избыток серотонина ведет к панике, а недостаток к депрессии. \r\n\r\nВосполнить запасы серотонина в мозгу поможет яркий свет (желательно дневной), либо правильная диета - богатая триптофаном пища (хлеб, бананы, шоколад, инжир), в сочетании с углеводами (хлеб, рис, сахар, фруктоза). Углеводы стимулируют выделение инсулина, который кроме прочего повышает синтез белков телесных тканей из конкурентных триптофану белков в крови. Концентрация триптофана относительно остальных белков повышается, соответственно увеличивается количество молекул триптофана преодолевших гемато-энцефалический барьер и попавших в мозг.', '', 'left'),
(8, 4, 'Наркотики и счастье', 'happy_drugs.png', 'Наркотики и счастье', 'Попытки искусственного влияния на выработку  гормонов и нейромедиаторов вносят дисбаланс в организм. \r\n\r\nЭто приводят к логичной реакции в виде изменения уровня выработки естественных веществ. Как следствие - человеку становится труднее получать положительные эмоции от обычной жизни, без дополнительных препаратов.\r\nПриступы депрессии между приемами \"дозы\" и \"ломка\" при выходе из процесса зависимости - результат именно этих процессов. \r\n\r\nМы не рекомендуем применение каких бы то ни было веществ для достижения ощущения радости.', '', 'left'),
(9, 6, 'Спорт', 'happy_run.png', 'Счастье в спорте', 'Не только полезен, но дает подъем настроения в силу задействования анти-стрессового механизма и выплеска эндорфинов.', 'Получасовое занятие физ. нагрузками или растяжкой дает 1.5-2 часа радости.', 'right'),
(10, 6, 'Экстрим', 'happy_extreme.png', 'Радость экстрима', 'Экстремальное времяпрепровождение так же задействует анти-стрессовый процесс и эндорфины. \r\nНо цель в данном случае - получение самого стресса, нежели нагрузок на организм (хотя часто они идут в комплекте со стрессом).\r\nИменно стрессовая ситуация дает выплеск эндорфинов, и чем сильнее стресс, тем больше радости. \r\nВ таких развлечениях главное - безопасность жизни, своей и окружающих.', 'Любая стрессовая ситуация, заканчивающаяся без травм.', 'right'),
(11, 6, 'Секс', 'happy_sex.png', 'Интимная жизнь и счастье', 'При занятиях сексом с любимым / привлекательным партнером мы получаем радость. В процессе повышается уровень и дофамина и эндорфинов.\r\nКак и в остальных видах чувственного счастья, сила ощущений зависит от ограничений.', 'Для лучшего результата рекомендуются: желанный партнер, предварительные ласки, продолжительное соитие (дольше 10 мин), разнообразие.  \r\nНе смотря на то, что частота и продолжительность очень индивидуальны, можно усилить ощущения снизив частоту занятий (в пределах разумного). \r\nЧем реже происходит что-то желанное, тем сильнее мы этого хотим и больше счастья получаем от обретения.', 'right'),
(12, 6, 'Вкусная еда', 'happy_food.png', 'Вкусная пища и счастье', 'Сильно зависит от ограничений (диета, пост, и т. д.) и субъективных пристрастий.', 'Отношение к еде как к удовольствию, неспешный прием пищи, тщательное пережевывание, поиск и наслаждение удачными сочетаниями вкусов.', 'right'),
(13, 6, 'Красивая музыка', 'happy_music.png', 'Музыка и счастье', 'Сильно зависит от субъективных пристрастий.', 'Насладиться любимой музыкой через качественную акустическую систему или наушники, выучить слова и подпевать, если текст на иностранном языке - найти перевод.', 'right'),
(14, 6, 'Позитивная энергетика окружающих (позитивное настроение в группе людей)', 'happy_crowd.png', 'Позитивное настроение окружающих и счастье', 'Среди большого количества радующихся людей мы заряжаемся праздничным настроением и испытываем душевный подъем.', 'Во время праздника находиться в эпицентре событий и с улыбкой поздравлять окружающих. \r\nНапример в новогоднюю ночь.', 'right'),
(15, 7, 'Счастье любви', 'happy_love.png', 'Счастье любви', 'Любовь это обширное понятие и заслуживает отдельного внимания. \r\nНежные и романтичные отношения приносят сильнейшее кратковременное счастье.', '', 'left'),
(16, 7, 'Счастье душевного равновесия', 'happy_calm.png', 'Счастье душевного спокойствия', 'Полное удовлетворение своими ресурсами, достижениями и ролью в жизни.', 'Осознать разницу между желаниями и необходимыми потребностями.\r\nРадоваться тому, что есть.', 'left'),
(17, 7, 'Счастье победы', 'happy_victory.png', 'Счастье победы', 'Достижение желанного результата, к которому человек долго стремился.', 'Наметить цели для достижения в определенный период.\r\nПрикладывать все силы к их реализации.\r\nРадоваться достижению целей.', 'left'),
(18, 7, 'Счастье созидания', 'happy_creation.png', 'Cчастье созидания', 'Создание чего-то полезного или приносящего радость, приносит радость и счастье создателю.', 'Выписать список того, что в твоих силах создать полезного или приносящего радость другим людям.\r\nСоздавать.', 'left'),
(19, 7, 'Счастье осознания  (познание / самопознание)', 'happy_knowledge.png', 'Cчастье осознания', 'Осознание недоступных ранее пониманию истин или процессов.', 'Изучать интересующие темы, создавать и уточнять свое понимание сути.', 'left'),
(20, 7, 'Счастье созерцания', 'happy_contemplation.png', 'Cчастье созерцания', 'Просмотр субъективно прекрасного вида приносит эстетическое удовольствие и счастье.', 'Посещать места с красивым видом, возвышенности. Смотреть на небо на рассвете и закате солнца. Увидеть шедевры живописи.', 'left'),
(21, 7, 'Эмоциональные качели', 'happy_tragyfun.png', 'Cчастье эмоций', 'Резкая смена настроения в положительную сторону приносит значительно больше радости, нежели постепенная.', 'Неожиданные подарки, приятные неожиданности, хорошие фильмы, вдохновляющие видеоролики.', 'left'),
(22, 7, 'Счастье делать добро', 'happy_giving.png', 'Cчастье делать добро', 'Причинение счастья и добра другим приносит автору радость и счастье.', 'У каждого свои возможности делать добро. \r\nСамые простые способы: искренняя забота, бескорыстная помощь, пожертвования, благотворительность.', 'left'),
(23, 8, 'Дети', 'happy_children.png', 'Дети - наше счастье', 'Древняя мудрость гласит: дети - цветы жизни. \r\nЧерез них мы продолжаем свой род и передаем накопленные знания, им мы радуемся в старости.\r\n\r\nВ современном мире, при сложности родить/забеременеть традиционным способом, есть много дополнительных возможностей.', '', 'right'),
(24, 8, 'Семейное счастье', 'happy_family.png', 'Cемейное счастье', 'Стабильные семейные отношения - одна из основополагающих причин счастливой жизни. \r\nКрепкая семья это не только залог счастья, но и опора для великих свершений.', '', 'right'),
(25, 8, 'Дело жизни', 'happy_lifework.png', 'Дело жизни', 'Найти дело своей жизни, призвание - важная часть состояния счастливой жизни. \r\nТаким делом может стать как работа, так и хобби.', '', 'right'),
(26, 8, 'Цель жизни', 'happy_lifepurpose.png', 'Цель жизни', 'Осознание своего предназначения значительно упрощает все. \r\nЧем раньше человек осознает цель, тем больше у него времени и возможностей для достижения намеченного.', '', 'right'),
(27, 8, 'Вера', 'happy_believe.png', 'Вера и счастье', 'Сильная вера ведет к искренней любви к богу. Является не только источником железобетонной внутренней уверенности и мощным инструментом достижения любой цели, но и причиной праведной жизни.', '', 'right'),
(28, 8, 'Дружба', 'happy_friendship.png', 'Дружба и счастье', 'Осознание существования крепких взаимоотношений, основанных на взаимопонимании и взаимопомощи - источник жизнеутверждающей уверенности в своих силах.', '', 'right'),
(29, 9, '', '', '', 'Счастье не в том, чтобы делать всегда, что хочешь, а в том, чтобы всегда хотеть того, что делаешь.', 'Лев Толстой \r\n(русский писатель 19-20вв)', 'left'),
(30, 9, '', '', '', 'Счастье на стороне того, кто доволен.', 'Аристотель \r\n(древнегреческий философ и учёный 4 в до н. э.)', 'left'),
(31, 9, '', '', '', 'Счастье — это шар, за которым мы гоняемся, пока он катится, и который мы толкаем ногой, когда он останавливается.', 'Пьер Буаст \r\n(французский лексикограф и поэт 18-19вв)', 'left'),
(32, 9, '', '', '', 'Наше счастье зависит куда более от того, как мы встречаем события нашей жизни, чем от природы самих событий.', 'Александр фон Гумбольдт \r\n(немецкий учёный-энциклопедист, физик, метеоролог, географ, ботаник, зоолог и путешественник 18-19вв)', 'left'),
(33, 9, '', '', '', 'Не существует пути к счастью. Счастье — это и есть путь.', 'Уэйн Дайер \r\n(американский философ, писатель 20-21вв)', 'left'),
(34, 9, '', '', '', 'Когда ты делаешь что-то для других от души, не ожидая благодарности, кто-то записывает это в книгу судеб и посылает счастье, о котором ты даже не мечтал.', 'Анджелина Джоли \r\n(американская актриса, режиссёр и сценарист)', 'left'),
(35, 9, '', '', '', 'Неважно, что вы имеете, кто вы, где вы или что вы делаете чтобы быть счастливым или несчастным. Важно то, что вы думаете об этом.', 'Дейл Карнеги \r\n(американский педагог, психолог, писатель. 19-20вв)', 'left'),
(36, 9, '', '', '', 'Жизнь на десять процентов состоит из того, что вы в ней делаете, а на девяносто из того, как вы её принимаете.', 'Ирвинг Берлин \r\n(американский композитор, 19-20вв)', 'left'),
(37, 9, '', '', '', 'Почти все уверены, что будут счастливы в будущем, и уверены, что были счастливы в прошлом.', 'Пьер Буаст \r\n(французский лексикограф и поэт 18-19вв)', 'left'),
(38, 9, '', '', '', 'Счастье — это не обладание тем, чего желаешь, а желание того, чем обладаешь.', 'Ошо \r\n(индийский духовный лидер, 20век)', 'left'),
(39, 9, '', '', '', 'Хочешь быть счастливым, будь им.', 'Козьма Прутков \r\n(литературный персонаж, 19 век)', 'left'),
(40, 9, '', '', '', 'Cчастье не зависит от объёма внешних благ, которые мы урвали у жизни. Оно зависит только от нашего отношения к ним!', 'Александр Солженицын \r\n(русский писатель, поэт, общественный и политический деятель, 20-21вв)', 'left'),
(41, 9, '', '', '', 'Спроси себя, чего ты хочешь — богатства или счастья? \r\nЕсли богатства — знай, что оно ни благо, ни в твоих руках; если счастья — будь уверен, что оно и благо, и находится в твоей власти: первым судьба ссужает нас только на время, тогда как быть счастливым зависит от нас самих.', 'Эпиктет \r\n(древнегреческий философ 1-2вв)', 'left'),
(42, 9, '', '', '', 'Исключительное счастье человека – быть при своём постоянном любимом деле.', 'Немирович-Данченко \r\n(русский театральный режиссёр, драматург, писатель 19-20вв)', 'left'),
(43, 9, '', '', '', 'Счастье, как здоровье: когда оно налицо, его не замечаешь.', 'Михаил Булгаков \r\n(русский писатель, драматург 19-20вв)', 'left'),
(44, 9, '', '', '', 'Счастье увеличивается от того, что им делишься с другими.', 'Жюльен Офре де Ламетри \r\n(французский врач и философ 18век)', 'left'),
(45, 9, '', '', '', 'Большинство людей счастливы настолько, насколько они решили быть счастливыми.', 'Авраам Линкольн \r\n(американский государственный деятель, 16-й президент США 19век)', 'left'),
(46, 9, '', '', '', 'Когда одна дверь счастья закрывается, открывается другая, но мы часто не замечаем ее, уставившись взглядом в закрытую дверь. ', 'Хелен Келлер \r\n(американская писательница, политический деятель 19-20вв)', 'left'),
(47, 9, '', '', '', 'Высшее счастье в жизни - это уверенность в том, что вас любят. Любят ради вас самих, вернее сказать - любят вопреки вам.', 'Виктор Гюго \r\n(французский писатель 19век)', 'left'),
(48, 9, '', '', '', 'У счастья нет завтрашнего дня, нет у него и вчерашнего... у него есть только настоящее – и то не день, а мгновение. ', 'Иван Тургенев \r\n(русский писатель, поэт 19век)', 'left'),
(49, 9, '', '', '', 'Я рожден, и это все, что необходимо, чтобы быть счастливым.', 'Альберт Эйнштейн \r\n(физик-теоретик 19-20вв)', 'left'),
(50, 9, '', '', '', 'Большое препятствие для счастья – это ожидание слишком большого счастья. ', 'Бернар Ле Бовье де Фонтенель \r\n(французский писатель, учёный 17-18вв)', 'left'),
(51, 9, '', '', '', 'Если мы будем искать счастья, не зная, где оно, мы рискуем с ним разойтись.', 'Жан-Жак Руссо \r\n(французский философ, писатель 18век)', 'left'),
(52, 9, '', '', '', 'Заботясь о счастье других, мы находим свое собственное.', 'Платон \r\n(древнегреческий философ  5 век до н.э.)', 'left'),
(53, 9, '', '', '', 'Здоровый нищий счастливее больного короля.', 'Артур Шопенгауэр \r\n(немецкий философ 18-19вв)', 'left'),
(54, 9, '', '', '', 'Не гоняйся за счастьем: оно всегда находится в тебе самом.', 'Пифагор Самосский \r\n(древнегреческий философ, математик 6-5вв до н.э.)', 'left'),
(55, 9, '', '', '', 'Нет счастья, равного спокойствию.', 'Будда Шакьямуни \r\n(духовный учитель 7-6вв до н.э.)', 'left'),
(56, 9, '', '', '', 'Счастье – это аромат, который невозможно излить на других людей, не уронив на себя хотя бы несколько капель.', 'Кит Эмерсон \r\n(британский клавишник и композитор 20-21вв)', 'left'),
(57, 9, '', '', '', 'Счастье – это такое состояние сознания, которое рождается при реализации человеком своих ценностей.', 'Айн Рэнд \r\n(американская писательница, философ 20век)', 'left'),
(58, 9, '', '', '', 'Счастье исключает старость. Кто сохраняет способность видеть прекрасное, тот не стареет.', 'Франц Кафка \r\n(немецкий писатель 19-20вв)', 'left'),
(59, 9, '', '', '', 'Счастлив только тот, кто живет не будущим, а настоящим.', 'Людвиг Витгенштейн \r\n(австрийский философ и логик 19-20вв)', 'left'),
(60, 10, '', 'happy_medicine.png', 'Счастье в медицине', 'Эмоциональный фон значительно влияет на состояние здоровья человека. \r\nИздревле смех и радость считались одним из способов борьбы с болезнями. Целители древней Греции и Китая называли смех даром человечества, эмоцией, продлевающей жизнь. \r\nИ современные исследования подтверждают этот факт. Исследования Ганса Селье показали, что в депрессии люди болеют простудными заболеваниями на 50% чаще, а подверженность гневу, раздражению и другим отрицательным эмоциям, сокращает продолжительность жизни до 25 %. \r\n\r\nКогда человек испытывает смех и радость, организм получает выброс эндорфинов и дополнительно насыщается кислородом, что приводит к противовоспалительному эффекту и повышает иммунитет. Выброс эндорфинов в кровь способствует обезболиванию, подъему настроения и быстрому заживлению ран.\r\n\r\nСчастливые люди меньше болеют, выздоравливают быстрее, имеют более крепкое здоровье и дольше живут. \r\n', '', 'left'),
(61, 11, 'Гедонизм', 'happy_aristippe.png', 'Аристипп', 'Счастье это удовольствие. Все на свете есть удовольствие (добро) и страдания (зло).', 'Аристипп (5-4 вв до н. э.)', 'right'),
(62, 11, 'Эпикуреизм', 'happy_epicur.png', 'Эпикур Самосский', 'Счастье это отсутствие страданий и душевное равновесие. Счастливый человек невозмутим, безмятежен и самодостаточен. Желания человека безграничны, а возможности ограниченны. Поэтому следует ограничиться необходимыми потребностями, неудовлетворение которых ведет к страданию. \r\nДля счастливой жизни человеку необходимо отсутствие страданий, уравновешенность и дружба.', 'Эпикур (4-3 вв до н. э.)', 'right'),
(63, 11, 'Утилитаризм', 'happy_democrit.png', 'Демокрит Абдерский', 'Счастье это польза. \r\nЦенность любого поступка определяется его пользой. Если результат поступка приносит пользу, цель оправдывает средства. Вне зависимости от того, какие применялись средства (даже безнравственные) - если результат полезен людям, средства расцениваются как нравственные. \r\nНаиболее полезно то, что приносит счастье наибольшему количеству людей.', 'Демокрит (5-4 вв до н. э.), <br> Джереми Бентам (18-19вв)', 'right'),
(64, 11, 'Стоицизм', 'happy_zenon.png', 'Зенон Китийский', 'Счастье состоит в нравственном совершенстве, контроле над своими страстями и подавлении неразумных желаний. Удовольствие, отвращение, вожделение и страх следует избегать, руководствуясь здравым смыслом. Стоик осознает добро и зло, но переносит жизненные события бесстрастно. Поскольку радость и горе, так же как процветание и бедствия, предопределены самой природой. ', 'Зенон Китийский  (4-3 вв до н. э.), <br>Луций Сенека (1в до н. э. - 1в н. э.)', 'right'),
(65, 11, 'Аристотелизм', 'happy_aristotle.png', 'Аристотель', 'Счастье - разумная и добродетельная деятельность. Хорошие дела это и путь к счастью и основная его составляющая.', 'Аристотель (4 в до н. э.)', 'right'),
(66, 12, '', 'happy_eko.png', 'Счастье и экономика', 'Счастье, как экономический показатель, используется нечасто. \r\nВ 2006 г, организацией New Economics Foundation (NEF) был предложен <a href=\"https://ru.wikipedia.org/wiki/%D0%9C%D0%B5%D0%B6%D0%B4%D1%83%D0%BD%D0%B0%D1%80%D0%BE%D0%B4%D0%BD%D1%8B%D0%B9_%D0%B8%D0%BD%D0%B4%D0%B5%D0%BA%D1%81_%D1%81%D1%87%D0%B0%D1%81%D1%82%D1%8C%D1%8F\">Международный Индекс Счастья</a>  ( <a href=\"http://www.happyplanetindex.org\">Happy Planet Index</a> ). \r\nИндекс рассчитывается исходя из трех основных показателей: субъективная удовлетворенность жизнью людей, средняя продолжительность жизни и экологическое состояние.\r\nТаким образом оценивается способность государства обеспечить своим жителям счастливую жизнь на основе тех экономических и экологических реалий, которые существуют в стране на сегодняшний день. \r\n\r\n По итогам 2012г. наиболее высокий индекс счастья был зафиксирован в Коста-Рике, Вьетнаме и Колумбии. Самое низкое значение индекса – в Ботсване, Чаде и Катаре. Россия в списке занимает 122 место, США - 105 место.', '', 'left'),
(67, 13, 'Синяя птица', 'happy_bird_blue.png', 'Синяя птица счастья', 'В Европе птицей счастья принято считать сказочную птицу синего цвета. \r\nЦвет ее настолько синий, что ни одна из существующих птиц недостаточно синяя.', '', 'right'),
(68, 13, 'Птица  Феникс', 'happy_phenix.png', 'Птица  Феникс', 'В Египте, Греции и Риме птицей счастья считают мифическую птицу Феникс. В конце жизни Феникс сгорает и возрождается из своего пепла.  Оперение Феникса красно-золотого цвета.', '', 'right'),
(69, 13, 'Птица Фэнхуан', 'happy_fenghuang.png', 'Птица Фэнхуан', 'В Китае так же известна бессмертная птица Фэнхуан. \r\nРостом в 3 метра птица имеет «клюв петуха, зоб ласточки, шею змеи, на туловище узоры, как у дракона, хвост рыбы, спереди как лебедь, сзади как единорог, спина черепахи». По некоторым данным китайский феникс имеет пурпурную окраску.', '', 'right'),
(70, 13, 'Птица счастья Поморья', 'happy_bird_ru.png', 'Птица счастья Поморья', 'В русском Поморье (южный берег Белого моря), птицей счастья считается деревянный оберег в виде птицы. \r\nТакой оберег вешается под потолок в прихожей. Каждый входящий, проходя под этой птицей оставляет все плохое и приносит в дом только хорошее и светлое.', '', 'right'),
(71, 13, 'Дерево счастья Драцена', 'happy_dracaena.png', 'Дерево счастья Драцена', 'В Америке деревом счастья называют драцену. \r\nСогласно ацтекской легенде, воин, полюбивший дочь жреца, попытался получить согласие на брак. Жрец выдвинул жестокие условия: воин должен в течении 5-ти дней поливать воткнутую жрецом в землю палку. Если через 5 дней на палке появятся листья - жрец отдаст дочь в жены воину, если листьев не появится - воина ждет смерть. В итоге листья на палке через 5 дней появились, и воин получил согласие жреца на брак. А из палки выросла драцена.', '', 'right'),
(72, 13, 'Топиарий', 'happy_topiary.png', 'Топиарий', 'Деревом счастья так-же часто называют топиарий, искусственное декоративное дерево с изогнутым стволом и идеально круглой кроной.', '', 'right'),
(73, 13, 'Цветок счастья', 'happy_spatifilum.png', 'Цветок счастья - Спатифиллум', 'Согласно научным исследованиям цветы в качестве подарка вызывают мгновенный всплеск счастья у людей всех возрастов. Цветы приносят длительный подъем настроения. После принятия цветов снижается подавленность настроения, тревожность. \r\n\r\nЖенским счастьем называют спатифиллум. Согласно поверью, подаренный женщине цветок поможет одинокой женщине найти любовь, а бездетной - забеременеть.', '', 'right'),
(74, 13, 'Иероглиф счастья', 'happy_ierogliph.png', 'Иероглиф счастья', 'Google переводчик гласит: \r\nСчастье - 幸福 (традиционный китайский язык) \r\n\r\nПо другим источникам можно найти другую информацию:\r\n吉 - иероглиф \"Счастье, удача\"\r\n福 - иероглиф \"Счастье, благополучие, удача\". В значении долговременного, стабильного счастья и благополучия.', '', 'right'),
(75, 14, '', 'happy_kalendar.png', 'День счастья', '20 марта - Международный День Счастья (назначен в 2012 г резолюцией ООН). \r\nПразднование этого события призвано укрепить стремление к счастью, как общее чувство, для всех людей планеты.', '', 'left'),
(76, 15, '', 'happy_museum.png', 'Музей счастья', 'В России есть Музей Счастья. \r\nНаходится он в Новосибирске, по адресу ул.1905 года, д13. \r\nЭкскурсии проводятся по предварительной записи, \r\nтелефон +7 (905) 934-9357. \r\n<a href=\"https://new.vk.com/club13475166\" >группа музея Вконтакте</a>', '', 'right');
