INSERT INTO `lo_ex` (`id`, `view_order`, `private`, `ex_name`, `ex_description`, `ex_level`, `ex_duration`, `ex_cat_id`, `warn`, `thanx`) VALUES
(1, 1, 0, 'Радость жизни', 'Осознание радости в своей жизни', 2, '00:15:00', 3, 'Упражнение требует спокойного состояния и отсутствия тревожащих факторов. \r\n\r\nДля оптимальных результатов необходимо полное погружение.', 'Надеемся, что погружение было глубоким и получена масса позитивных эмоций.'),
(2, 2, 0, 'Поиск призвания 1', 'Поиск жизненного призвания', 3, '00:20:00', 3, 'Если в целом жизнь приносит удовлетворение, не беспокойся - ты на своем месте.\nА когда чего-то не хватает, всегда есть место для любимого хобби.\nТем не менее, мы всегда можем улучшить жизнь. Следующие упражнения помогут заглянуть в себя и узнать, что необходимо для достижения лучшего жизненного равновесия.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.'),
(3, 3, 1, 'Поиск призвания 2', 'Глубокая проработка, направленная на поиск жизненного призвания', 1, '01:00:00', 2, 'Это упражнение серьезное.\n\nДля получения результата, необходимо полное непрерывное погружение. \n\nПродолжайте, если есть 30 минут свободного времени.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nДля лучшего глобального результата, желательно исключить прохождение других занятий сегодня. \n\nОбдумай полученные результаты и эмоции.'),
(4, 0, 0, 'Колесо жизни', 'Проработка сфер жизни.', 2, '00:15:00', 2, NULL, NULL),
(5, 4, 0, 'Жажда перемен', 'Упражнение, направленное на понимание каких перемен ты хочешь в жизни.', 2, '00:20:00', 4, 'Если в целом жизнь приносит удовлетворение, не беспокойся - ты на своем месте.\nА когда чего-то не хватает, всегда есть место для любимого хобби.\nТем не менее, мы всегда можем улучшить жизнь. Следующие упражнения помогут заглянуть в себя и узнать, что необходимо для достижения лучшего жизненного равновесия.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.'),
(6, 5, 1, 'Самоуважение', 'Упражнение для осознания и усиления уважения к себе.', 2, '00:30:00', 6, 'Упражнение требует спокойного состояния и отсутствия тревожащих факторов. Для оптимальных результатов необходимо полное погружение.\n\nЧем серьезней ты относишься к выполнению задания, тем лучше и ярче результат.', 'Надеемся, что упражнение было полезным.\nА в процессе его прохождения - найдены интересные мысли.\n\nУ нас есть еще много интересных и глубоких упражнений.');
INSERT INTO `lo_steps` (`id`, `exercise_id`, `step_number`, `step_text`, `step_duration`) VALUES
(1, 1, 1, 'Найди исправную ручку и чистый лист бумаги.', '00:05:00'),
(2, 1, 2, 'Вспомни, что приносит тебе удовольствие в жизни.\n\nНапиши 10 пунктов.', '00:05:00'),
(4, 1, 3, 'Мысленно погрузись в каждое из написанных действий. \r\nНе спеши, представь во всех красках. \r\nПочувствуй свои эмоции относительно происходящего.', '00:15:00'),
(5, 1, 4, 'Вспомни 3 самых счастливых момента в своей жизни.\r\n\r\nЗапиши на обратной стороне листа бумаги.', '00:05:00'),
(6, 1, 5, 'Мысленно погрузись в каждое событие.', '00:10:00'),
(7, 1, 6, 'Найди одно слово, характеризующее эмоцию, вызываемую этим событием.\r\n\r\nЗапиши напротив каждого события на листе.', '00:05:00'),
(8, 2, 1, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', '00:02:00'),
(9, 2, 2, 'Вспомни, какими занятиями тебе нравилось заниматься в своей жизни.\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', '00:05:00'),
(10, 2, 3, 'Какое дело по твоему мнению, тебе удается лучше всего?\r\nДелая что, ты получаешь максимальное внутреннее удовлетворение от результата?\r\n\r\nПодчеркни.', '00:05:00'),
(11, 4, 1, 'Оцени уровень развития твоей жизни в области семьи и личной жизни. \r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(12, 4, 2, 'Оцени уровень развития твоей жизни в области друзей и окружения\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(13, 4, 3, 'Оцени уровень развития твоей жизни в области здоровья и спорта\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(14, 4, 4, 'Оцени уровень развития твоей жизни в области развлечений и яркости жизни\r\nНасколько часто ты развлекаешься и весело живешь?\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(15, 4, 5, 'Оцени уровень развития твоей жизни в области творчества и духовности\r\nТы творческий человек?\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(16, 4, 6, 'Оцени уровень развития твоей жизни в области бизнеса и карьеры\r\nУспех тебе сопутствует?\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(17, 4, 7, 'Оцени уровень развития твоей жизни в области финансов\r\nТебе на жизнь хватает?\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(18, 4, 8, 'Оцени уровень развития твоей жизни в области личностного роста\r\nТы развиваешься?\r\nПоставь цифру от 1 до 10 в качестве оценки', '00:02:00'),
(19, 4, 9, 'Взгляни на твою оценку своей жизни.\r\nКакие сферы ты считаешь можно подтянуть для сбалансированного развития?\r\nКакими способами можно усилить слабые сферы?\r\nНапиши 3 способа развития каждой слабой сферы и отправь себе на e-mail', '00:20:00'),
(20, 2, 4, 'Ради каких дел ты с удовольствием просыпался бы с утра?\r\n\r\nПодчеркни.\r\n(если это занятие уже подчеркнуто, подчеркни второй линией ниже)', '00:02:00'),
(21, 2, 5, 'Взгляни на записанную информацию.\r\nКакие тенденции просматриваются?\r\n\r\nВыпиши 1-3 основных мысли.', '00:05:00'),
(22, 2, 6, 'Оцени, насколько выписанные тенденции важны для тебя.\r\nДостаточное ли место они занимают в твоей настоящей жизни?', '00:05:00'),
(23, 3, 1, 'Найди 2-3 чистых листа бумаги (или тетрадь, блокнот) и исправную ручку.', '00:03:00'),
(24, 3, 2, 'Какие дела тебя раздражают, что не нравится делать? \r\nЗапиши на листе 1-3 занятий.', '00:05:00'),
(25, 3, 3, 'Напротив каждого пункта напиши, что именно в этом действии выводит тебя из равновесия.', '00:00:00'),
(26, 3, 4, 'Зачеркни каждый из записанных пунктов. \r\nОставь только причину - то что раздражает.', '00:02:00'),
(27, 3, 5, 'Какие эмоции тебе (в идеале) хотелось бы испытывать при работе? \r\nЧто бы тебе хотелось испытывать, что бы начинать новый рабочий день с удовольствием?\r\n\r\nЗапиши 3-5 пунктов.', '00:05:00'),
(28, 3, 6, 'Какое дело по твоему мнению тебе удается лучше всего?\r\nДелая что, ты получаешь максимальное внутреннее удовлетворение от результата?\r\n\r\nЗапиши это дело.', '00:03:00'),
(29, 3, 7, 'Вернись в молодость, в переходный период, 13-18 лет. \r\nЭто замечательное время, когда ты уже представляешь себе как устроен мир, но еще нет ограничений типа - “А что если у меня не получится\", и т. д. \r\nЧем ты увлекаешься? \r\nКакие у тебя собственные мечты, не навязанные взрослыми?\r\n\r\nЗапиши на бумаге одно или несколько занятий, которыми мечтаешь заниматься, когда вырастешь.', '00:05:00'),
(30, 3, 8, 'Возвращаемся в настоящее время. \r\nЧто ты любишь делать (кроме сна и просмотра любимых сериалов)?\r\n\r\nНапиши список из 5-10 занятий, которые приносят тебе удовольствие в этот период жизни.\r\nВ список могут быть включены активные виды отдыха и спорт.\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', '00:10:00'),
(31, 3, 9, 'Вспомни, какими занятиями тебе нравилось заниматься ранее в своей жизни.\r\n\r\nЕсли есть такие, которые не входят ни в один из записанных уже списков - напиши их отдельно.\r\n\r\nНапротив каждого пункта напиши одну эмоцию, характеризующую для тебя это дело.', '00:03:00'),
(32, 3, 10, 'Иногда делая что-то, ты настолько полностью погружаешься в это процесс, что забываешь о течении времени.\r\n\r\nЧто это за дело? Какое из описанных занятий наиболее подходит под такое определение?\r\n\r\nПодчеркни.', '00:03:00'),
(33, 3, 11, 'Ради каких дел ты с удовольствием просыпался бы с утра?\r\n\r\nПодчеркни. \r\n(если это занятие уже подчеркнуто, подчеркни второй линией ниже)', '00:05:00'),
(34, 3, 12, 'Представь себе людей с которыми тебе было бы комфортно общаться. \r\n\r\nЭто люди аналитического склада или творческого? \r\nОбщительны или молчаливы? \r\nКак их можно охарактеризовать? \r\nЧто помогает, заряжает и вдохновляет тебя в таком общении?\r\n\r\nЗапиши:\r\n1) Род деятельности этих людей.\r\n2) Что их объединяет?\r\n3) Почему тебе хорошо с ними? \r\n4) Чем тебе помогает это общение?', '00:05:00'),
(35, 3, 13, 'Взгляни на записанную информацию.\r\nКакие тенденции просматриваются?\r\n\r\nВыпиши 1-3 основных мысли.', '00:05:00'),
(36, 3, 14, 'Оцени, насколько выписанные тенденции важны для тебя.\r\nДостаточное ли место они занимают в твоей настоящей жизни?\r\nИмеет ли смысл развить какие либо из твоих навыков дополнительным обучением?\r\nНеобходимо ли уделить чему-то определенное время, если оно неоправданно забыто?\r\n\r\nЗапиши основной вывод из проделанного упражнения и отправь себе на e-mail, что бы переосмыслить на свежую голову.', '00:05:00'),
(37, 5, 1, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', '00:03:00'),
(38, 5, 2, 'Что ты хочешь изменить в своей жизни?\r\n\r\nНапиши 5 пунктов.', '00:05:00'),
(39, 5, 3, 'По каждому пункту напиши 3 варианта решения. ', '00:05:00'),
(40, 5, 4, 'Представь что эти перемены уже произошли. \r\nКак это отразилось на остальных сферах жизни?\r\nКак это отразилось на близких тебе людях?', '00:05:00'),
(41, 5, 5, 'Сделай выводы из проделанного упражнения.', '00:05:00'),
(42, 6, 1, 'Найди  чистый лист бумаги (или тетрадь, блокнот) и исправную ручку.', '00:03:00'),
(43, 6, 2, 'За что тебя ценят люди?\r\n\r\nВыпиши 5-10 пунктов.', '00:05:00'),
(44, 6, 3, 'Какой из пунктов ты считаешь наиболее важным?  \r\n\r\nПодчеркни.', '00:02:00'),
(45, 6, 4, 'Что ты умеешь делать лучше всего?\r\n\r\nВыпиши 3-10 пунктов.', '00:05:00'),
(46, 6, 5, 'Какой из навыков ты считаешь наиболее важным?  \r\n\r\nПодчеркни.', '00:02:00'),
(47, 6, 6, 'Чего ты добился в своей жизни?\r\n\r\nВыпиши 1-5 пунктов.', '00:05:00'),
(48, 6, 7, 'Чего ты еще планируешь добиться в жизни?\r\n\r\nВыпиши 1-5 пунктов.', '00:05:00'),
(49, 6, 8, 'Подчеркни: \r\n\r\nсамое важное достижение прошлого \r\n\r\nсамую важную цель в будущем', '00:02:00'),
(50, 6, 9, 'Взгляни на записанную информацию.\r\nОбрати внимание на все подчеркнутые пункты, постарайся их запомнить.', '00:03:00'),
(51, 6, 10, 'Последующие действия поместят тебя в определенную позу. \r\n\r\nДействия не должны вызывать дискомфорт. \r\n\r\nПри появлении дискомфорта, ослабь вызвавшее его движение.', '00:02:00'),
(52, 6, 11, 'Встань прямо, опираясь на одну ногу, поставь другую на что-либо высотой 20-30см. \r\n\r\nРуки согни, ладони на пояс (положение “Руки в боки”). \r\n\r\nВыпрями спину максимально прямо (положение “Грудь колесом”).\r\n\r\nПриподними подбородок.\r\n\r\nЗакрой глаза и закрытыми глазами посмотри чуть выше горизонта (там где обычно находится утром солнце).\r\n\r\nВспомни выделенные пункты.\r\n\r\nНаполнись уверенностью и энергией, почувствуй себя хозяином своей жизни. \r\n\r\nВ этой позе проведи 1-5 минут.', '00:05:00');
INSERT INTO `masters` (`id`, `name`, `last_name`, `email`, `hrurl`, `title`, `description`, `keywords`, `lead_text`, `image`, `page_description`, `imagelink`, `imagelink_alt`, `sendtopage`, `promolink`, `promoname`, `status`) VALUES
(1, 'Мария', 'Лялина', 'nashe-schastye@yandex.ru', 'ml', 'Наше счастье | Мария Лялина', 'Мария Лялина - мастер телесной терапии, практикующий психолог, мастер динамических медитаций, йога – терапевт', 'психологические тренинги, телесная терапия, телеска,  активная медитация, динамическая медитация, дыхательные техники, познай свое тело, познай себя', 'Более 5 лет практики ● десятки тренингов ● сотни благодарностей', 'masha_2.jpg', 'Проводит мастер классы для профессиональных психологов, практические семинары телесных практик, групповые и индивидуальные терапевтические сессии и консультации. \r\n\r\nНиже перечислен частичный список кардинальных перемен, происходящих с пациентами после ее сессий.\r\nПосле терапии участники  растут в бизнесе, увеличивают доход, раскрывают свою сексуальность, возвращают страсть в отношения, начинается медовый месяц спустя десятки лет брака, жены становятся мягче и добрее, происходит сближение с родителями, развивается чувствительность и чуткость по отношению к близким, расширяется спектр телесных ощущений, приходят замужества, после долгих лет неудачных попыток зачатия рождаются дети, появляется жизненная реализация, раскрытие творческих способностей.\r\n\r\nВ тренингах использует динамические медитации, техники работы с бессознательным, крии из кундалини йоги.', '', '', '', 'liveout', 'Проживание', 'active'),
(2, 'Айгуль', 'Ше', 'quadrosh@gmail.com', 'ishe', 'Наше счастье | Айгуль Ше', 'Айгуль Ше - психолог-консультант, мастер рейки, тренер личностного развития', 'Айгуль Ше - психолог-консультант, мастер рейки, тренер личностного развития, мастер телесной терапии, телеска,  активная медитация, динамическая медитация, дыхательные техники, познай свое тело, познай себя', '', 'ishe.jpg', 'Помощь в преодолении кризисных состояний, в поиске себя и своего предназначения. Наставничество на пути раскрытия своей личности и её потенциала, в развитии осознанности для со-творения желанной гармоничной реальности. А так-же консультации по вопросам взаимоотношений с родителями, с партнером и в решении личностных проблем в различных сферах жизни.', '', '', '', 'liveout', 'Проживание', 'draft');
