INSERT INTO `master_sessions` (`id`, `master_id`, `name`, `discription`, `form_1`, `price_1`, `currency_1`, `form_2`, `price_2`, `currency_2`, `cta_name`, `cta_link`) VALUES
(1, 1, 'Индивидуальные консультации', 'На первой консультации определяется тема, которой будут посвящены встречи и намечается ряд задач, решение которых приведет к желаемой цели. Регулярность встреч зависит от внутреннего ощущения пациента. Рекомендуется 1 встреча в неделю, 2 встречи – при крайней необходимости.', 'Консультация', 1000, 'р/час', '', NULL, '', 'Записаться', 'send/to'),
(2, 1, 'Индивидуальные терапевтические сессии', 'В индивидуальной терапии проводит консультации и терапевтические сессии (длительность 1,5ч - 3,5ч). Для комплексного решения актуальной проблемы логичен курс из 3-7 консультаций или 2-5 терапевтических сессий. Телесно – ориентированная терапия осуществляется лежа на коврике, стоя, сидя. С собой необходимо взять удобную одежду, которая не будет стеснять ваших движений, обувь не нужна.', 'Консультация', 1000, 'р/час', '', NULL, '', 'Записаться', 'send/to'),
(3, 1, 'Скайп консультация', 'Поскольку работая на расстоянии терапевт не имеет возможности прикоснуться к телу, при любом запросе используются техники работы с бессознательным (спектр вопросов обширный, тема выбирается на первой консультации). В конце терапии пациент получает домашнее задание из простых телесных практик, которые помогут простроить новые стратегии поведения, навыки, ощущения, состояния на уровне тела.', 'Консультация', 1000, 'р/1,5часа', 'Терапевтическая сессия', 3000, 'р', 'Записаться', 'send/to'),
(4, 1, 'Групповые терапевтические сессии', 'Группа от 5-ти человек. Как правило, в начале каждой встречи выбирается тема, с которой предстоит поработать. Каждый участник рассказывает тревожащих его проблемах, с какими из них он готов расстаться, чем бы он хотел бы их заменить. Затем мастер предлагает ряд парных или групповых упражнений и отслеживает корректность их выполнения. Встреча завершается ритуалом обратной связи. Участники делятся опытом, прожитым в процессе упражнении. Встреча группы обычно происходит 1–2 раза в неделю, в зависимости от запросов группы.', 'Групповая сессия', 1500, 'р/2,5часа', '', NULL, '', 'Записаться', 'send/to'),
(5, 1, 'Тренинги', 'Тренинг включает в себя комплексную групповую терапию (2 дня по 8 часов).', '', NULL, '', '', NULL, '', 'Задать вопрос относительно тренинга', 'send/to'),
(6, 2, 'Индивидуальные консультации', 'Преимущество личных встреч в том, что есть возможность проработать различные нюансы, являющиеся помехой в достижении целей, такие как: ощущение бессмысленности, нехватка мотивации, усталость, накопленные не выраженные эмоции, лежащие блоками в теле и многое другое, используя метод телесно-ориентированной психотерапии, дыхательные техники, классические и динамические медитации.', 'Консультация', 1000, 'р/час', '', NULL, '', 'Записаться', 'send/to'),
(7, 2, 'Скайп консультация', 'Данный вид консультации удобен тем, что сметает любые расстояния. В работе используются те же методы, что и при личной встрече, за исключением телесно-ориентированной терапии. \r\nНесмотря на отсутствие непосредственного контакта телесные практики возможны - в виде несложных домашних упражнений направленных на закрепление новых стратегий поведения и осознания на уровне тела. \r\nДополнительно при необходимости и по договорённости могут использоваться тетахиллинг или нейротрансформинг.', 'Консультация', 1000, 'р/1,5часа', '', NULL, '', 'Записаться', 'send/to'),
(8, 2, 'Тренинги', 'Тренинг включает в себя комплексную групповую терапию (2 дня по 8 часов).', '', NULL, '', '', NULL, '', 'Задать вопрос относительно тренинга', 'send/to');
