INSERT INTO `pages` (`id`, `title`, `description`, `keywords`, `pagehead`, `pagedescription`, `imagelink`, `imagelink_alt`, `sendtopage`, `promolink`, `promoname`) VALUES
(1, 'Наше Счастье', 'В поисках счастья - знания, техники, практики.', 'техники счастья, практики достижения душевного равновесия, методы достижения счастья в жизни', 'Наше Счастье', '', NULL, '', '', '', ''),
(2, 'Мотиваторы', 'Подборки мотивирующих фраз, \r\nсобранных по определенной тематике и настраивающих на позитив. \r\nНаши мотиваторы разделены по позиции восприятия.', 'Мотиваторы, цитаты, как набраться сил', 'Мотиваторы', 'Мотиватор это подборка мотивирующих фраз, \r\nсобранных по определенной тематике и настраивающих на позитив. \r\n\r\nНаши мотиваторы разделены по позиции восприятия.', '', '', 'motivator/accept-the-life', 'liveout', 'Проживание'),
(3, 'Наше счастье - Проживание', 'Проживание - набор упражнений, направленных на самопознание или позитивные эмоциональные переживания.', 'переживания, самопознание, поиск себя, познать себя', 'Проживание', 'Проживание - набор несложных упражнений, \nпризванных вызвать яркие эмоциональные переживания, \nлибо глубже познать себя.', NULL, '', 'liveout/why-it-works', 'liveout/why-it-works', 'Почему это работает'),
(4, 'Библиотека', 'Информация - психологические тренинги и методики ', 'методы достижения счастья, психологические тренинги, телесная терапия, телеска,  активная медитация, динамическая медитация, дыхательные техники', 'Библиотека', 'Ниже собрана информация, полезная для саморазвития \r\nи достижения душевного равновесия. ', '', '', 'liveout', 'liveout', 'Проживание'),
(5, 'Наше счастье | Телесно ориентированные терапевты', 'Телесно ориентированные терапевты, мастера телесной терапии, йога медитаций, динамических медитаций, холотропного дыхания, энергетического восстановления организма', 'Телесно ориентированные терапевты, мастера телесной терапии', 'Мастера', '', '', '', '', 'liveout', 'проживание'),
(6, 'Наше счастье | Тренинги телесной психотерапии', 'Тренинги телесной психотерапии это путь достижения счастья и душевного равновесия через познание себя и своего тела. Вас ожидают: динамические медитации, дыхательные техники, практики глубокой релаксации, снятие телесных зажимов, энергетическое восстановление организма. Приглашаем в увлекательное путешествие по эмоциям и телесным ощущениям, наполненное радостью и светом. ', 'психологические тренинги, телесная психотерапия, телесная терапия, телеска, работа с телом, активная медитация, динамическая медитация, холотропное дыхание, дыхательные техники, самопознание, познай себя', 'Тренинги', 'Тренинги телесной психотерапии - путь достижения счастья, радости и душевного равновесия через работу над собой на уровне тела. \r\nНа тренинге Вы прорабатываете свою жизненную ситуацию и снимаете телесные блоки, мешающие Вам жить счастливо. \r\nСреди методик: динамические медитации, дыхательные техники, практики глубокой релаксации, снятие телесных зажимов, энергетическое восстановление организма. ', '', '', 'liveout', 'liveout', 'проживание'),
(7, 'Счастливый цитатник', 'Интерактивные цитаты о счастье', 'Цитаты о счастье, высказывания о счастье, суть счастья по мнению великих,  мнение знаменитостей о счастье', 'Счастливый цитатник', 'Интерактивные цитаты выдающихся людей о счастье', '', '', 'quotepad/1', 'quotepad/1', 'Смотреть цитату');
