INSERT INTO `trainings` (`id`, `master_id`, `date`, `t_when`, `city`, `t_where`, `full_price`, `currency`, `discount`, `title`, `description`, `keywords`, `pagehead`, `pagedescription`, `topimage`, `big_text`, `small_text`, `order_text`, `text1`, `text2`, `text3`, `order_backimage`, `action_1_name`, `action_1_go`, `action_1_link`, `action_1_backimage`, `action_2_name`, `action_2_go`, `action_2_link`, `action_2_backimage`, `imagelink`, `imagelink_alt`, `sendtopage`, `promolink`, `promoname`, `view`, `map`) VALUES
(1, 1, '27-02-2016', '27-28 февраля 2016', 'Москва', 'м. Баррикадная, Кудринский переулок, д. 3Б.', 15000, 'руб', 20, 'Наше счастье | Практики улучшения качества жизни', 'Тренинг телесной психотерапии', 'Тренинг телесной психотерапии', 'Практики улучшения качества жизни', '2-х дневный тренинг телесной психотерапии <br> ориентированный на решение личностных задач', 'space_bgr.jpg', '', '', 'Практики включают в себя элементы динамической медитации, дыхательных психотехник и проработку самосознания. Двухдневный тренинг - наиболее эффективный метод телесно ориентированной терапии. На два дня вы полностью погружаетесь в снятие внутренних блоков, активизацию жизненной энергии и выстраивание новых жизненных стратегий. Вы сможете прикоснуться к глубинным переживаниям, найти корни проблем, снять бремя старых обид и разочарований, духовно и физически обновиться.\r\n\r\nВы наполнитесь энергией, выйдете из состояния тревоги и улучшите свою жизнь во всех направлениях.', '', '', '', 'par_sub-bgr.jpg', 'Поможет ли телесная психотерапия Вам', 'Узнать', 'send/help', '', 'Получить бесплатную консультацию по своей жизненной ситуации', 'Получить', 'send/action', 'inside_man.jpg', '', '', 'library/body-oriented-therapy', 'send/training', 'отправить заявку', 'firstright', '<script type=\"text/javascript\" charset=\"utf-8\" src=\"https://api-maps.yandex.ru/services/constructor/1.0/js/?sid=JkjYL1X7XbsUNe9ARZSXpYRZdRlKrubS&amp;height=480\"></script>'),
(2, 1, '28-05-2016', '28-29 мая 2016', 'Москва', 'м. Баррикадная, Кудринский переулок, д. 3Б.', 15000, 'руб', 20, 'Мария Лялина - Интенсив телесных практик \"Радость тела\"', 'Интенсив телесной психотерапии Марии Лялиной \'Радость тела\' - путь достижения счастья и душевного равновесия через познание себя и своего тела. Двухдневный тренинг телески проходит в Москве 28 и 29 мая 2016г. Приглашаем в увлекательное путешествие по эмоциям и телесным ощущениям, наполненное светом и истинной радостью. Вас ожидают: динамические медитации, дыхательные техники, практики глубокой релаксации, снятие телесных зажимов, энергетическое восстановление организма. ', 'психологические тренинги, телесная психотерапия, телесная терапия, телеска, работа с телом, активная медитация, динамическая медитация, холотропное дыхание, дыхательные техники, самопознание, познай себя', 'Интенсив телесных практик <br> \"Радость тела\"', '2-х дневный тренинг телесной психотерапии <br> ориентированный на проработку личных запросов', 'space_bgr2.jpg', '', '', '<span class=\"header\">Интенсив телесных практик</span> \r\nПрактики включают в себя элементы динамической медитации, дыхательных психотехник и проработку самосознания. Двухдневный тренинг - наиболее эффективный метод телесно ориентированной терапии. На два дня вы полностью погружаетесь в снятие внутренних блоков, активизацию жизненной энергии и выстраивание новых жизненных стратегий. Вы сможете прикоснуться к глубинным переживаниям, найти корни проблем, снять бремя старых обид и разочарований, духовно и физически обновиться.\r\n\r\nВы наполнитесь энергией, выйдете из состояния тревоги и улучшите свою жизнь во всех направлениях.', '', '', '', 'par_sub-bgr.jpg', 'Поможет ли телесная психотерапия Вам', 'Узнать', 'send/help', '', 'Получить бесплатную консультацию по своей жизненной ситуации', 'Получить', 'send/action', 'inside_man.jpg', '', '', 'library/body-oriented-therapy', 'send/training', 'отправить заявку', 'view', '<script type=\"text/javascript